mapper.select:
select p.photoBoard_id as photoBoard_id, title, saveName, p.member_id as member_id, thumb1, thumb2, hit, p.regdate as regdate, nickname, levelname, count(photoComment_id) as count from photoBoard p left outer join photoComment c on p.photoBoard_id=c.photoBoard_id left outer join member m on p.member_id=m.member_id left outer join level l on l.level_id=m.level_id
where title like CONCAT('%', #{searching.keyword}, '%') where nickname like CONCAT('%', #{searching.keyword}, '%')
group by p.photoBoard_id order by p.photoBoard_id desc limit #{startContent}, #{pageSize}

select photoBoard_id, title, saveName, p.member_id, thumb1, thumb2, hit, regdate, nickname, levelname
from photoBoard p, member m, level l
where p.member_id = m.member_id and m.level_id = l.level_id and photoBoard_id = #{photoBoard_id}
